import React from 'react';
import { Row, Col, Button } from 'react-bootstrap';
import { useCart } from './Cartcontext';

function CartPage() {
  const { cartItems, removeFromCart } = useCart(); // Get cartItems and removeFromCart functions

  return (
    <div>
      <Row>
        <Col sm={12}>
          <h2>Your Cart</h2>
          {cartItems.length === 0 ? (
            <p>No items in cart.</p>
          ) : (
            cartItems.map((item) => (
              <div key={item.id} className='d-flex justify-content-between align-items-center py-3'>
                <div>
                  <img src={item.image} alt={item.brand} style={{ width: "100px" }} />
                  <p>{item.brand} - {item.Display}</p>
                </div>
                <Button 
                  variant='danger' 
                  onClick={() => removeFromCart(item.id)} 
                >
                  Remove
                </Button>
              </div>
            ))
          )}
        </Col>
      </Row>
    </div>
  );
}

export default CartPage;
