import React from 'react';
import Sidenav from '../Sidenav';
import { product2Datas } from '../../Data/Product2';
import { useParams } from 'react-router-dom';
import { Row, Col } from 'react-bootstrap';
import { useCart } from '../../Cart/Cartcontext';
import Topbar from './Topbar';

function Headdetails() {
  const { id } = useParams();
  const productData = product2Datas.find((e) => e.id === id);
  
  // Use the cart context for adding items to the cart
  const { addToCart } = useCart(); 

  if (!productData) {
    return <div>Product not found</div>; // In case the product is not found
  }

  return (
    <div>
      <Topbar />
      <Row>
        <Col sm={2}>
          <Sidenav />
        </Col>
        <Col sm={10}>
          <div className='container d-flex justify-content-around align-items-center py-4'>
            <div className='data'>
              <p>ID: {productData.id}</p>
              <p>Brand: {productData.brand}</p>
              <p>Frequency: {productData.Frequency}</p>
              <p>Model: {productData.Model}</p>
              <p>Connectivity: {productData.Connectivity}</p>
              <p>Price: {productData.Price}</p>
              <button 
                className='btn btn-outline-primary' 
                onClick={() => addToCart(productData)} // Directly call addToCart from context
              >
                Add to cart
              </button>
            </div>
            <div className='product'>
              <img src={productData.image} style={{ width: "300px" }} alt={productData.Model} />
            </div>
          </div>
        </Col>
      </Row>
    </div>
  );
}

export default Headdetails;
