import React, { useState } from 'react';
import { Col, Row, Card } from 'react-bootstrap';
import { product2Datas } from '../Data/Product2'; // Assuming this is your product data
import Sidenav from './Sidenav';
import Phonenav from './Phonenav';
import { Link } from 'react-router-dom';

function Headphones() {
  const [selectedBrand, setSelectedBrand] = useState('All');

  // Apply the filter only if the selected brand is not 'All'
  const filteredProducts = selectedBrand === 'All'
    ? product2Datas
    : product2Datas.filter((product) => product.brand.toLowerCase() === selectedBrand.toLowerCase());

  return (
    <Row>
      <Col sm={2}>
        <Sidenav />
      </Col>
      <Col sm={10}>
        <Phonenav setSelectedBrand={setSelectedBrand} /> 
        <div className='tv'>
          {filteredProducts.length === 0 ? (
            <p>No products available for the selected brand.</p>  // Display a message when no products are found
          ) : (
            filteredProducts.map((h) => (
              <Col key={h.id}> 
                <Link to={`/headdetails/${h.id}`} className="text-decoration-none">
                  <Card className="card">
                    <Card.Body>
                      <img src={h.image} alt={h.brand} style={{ width: '200px', height: '150px' }} />
                      <Card.Title>{h.brand}</Card.Title>
                      <Card.Text>
                        <strong>Model:</strong> {h.Model} <br />
                        <strong>Frequency:</strong> {h.Frequency} <br />
                        <strong>Connectivity:</strong> {h.Connectivity} <br />
                        <strong>Price:</strong> {h.Price}
                      </Card.Text>
                    </Card.Body>
                  </Card>
                </Link>
              </Col>
            ))
          )}
        </div>
      </Col>
    </Row>
  );
}

export default Headphones;
